arr = string.split(' ');
str = arr[getRandomInt(0, arr.length)];

var strSuffle = shufstr(str);
var i = strSuffle.length;

for(var i=0; i<strSuffle.length; i++){
	var div = document.createElement('div');
	div.id = i+'_box';
	div.classList.add('box');
	div.innerHTML = strSuffle.charAt(i);
	div.setAttribute('draggable', true);
	div.setAttribute('ondragstart', 'dragstart(event)');
	div.setAttribute('ondragend', 'dragend(event)');
	blockContainer.appendChild(div);
}

for(var i=0; i<str.length; i++){
	var div = document.createElement('div');
	div.id = str.charAt(i)+'_block';
	div.classList.add('block');
	div.setAttribute('ondragover', 'allowDrop(event)')
	div.setAttribute('ondrop', 'drop(event, this)');
	div.setAttribute('ondragleave', 'dragleave(event)');
	boxContainer.appendChild(div);
} 

function shufstr(str){
	var shufstr = '';
	while(str.length){
		var b = Math.floor(Math.random()*(str.length));
		shufstr = shufstr + str.slice(b, b+1);
		str = str.slice(0, b) + str.slice(b+1);
	}
	return shufstr;
}

var boxId = '';
var lit = '';
var lith = '';
var effort = 0;

function dragstart(event){
	boxId = event.target.id;
	lit = event.target.textContent;
	setTimeout(function(){
		event.target.style.display = 'none';
	},0);
	
}

function dragend(event){
	setTimeout(function(){
		document.getElementById(boxId).style.display = '';
	},5);
}

function allowDrop(event){
	event.preventDefault();
	event.target.classList.add('activ');
	lith = event.target.id.charAt(0);
}

function dragleave(event){
	event.preventDefault();
	event.target.classList.remove('activ');
	lith = event.target.id.charAt(0);
}

function drop(event, block){
	if(lit == lith){
		block.appendChild(document.getElementById(boxId));
		event.target.classList.remove('activ');
		document.getElementById(boxId).classList.add('starr');
		document.getElementById(boxId).draggable = false;
		i--;
		if(!i){
			go.style.display = 'block';
		}
	}
	else {
		event.target.classList.remove('activ');
		effort++;
		document.getElementById('effort').innerHTML = "Fehlschlag: "+effort;
	}
}

function getRandomInt(min, max) {  
    return Math.floor(Math.random() * (max - min + 1)) + min;  
}

////////////////////////////////////
function size(){
	if(document.documentElement.clientWidth >= document.documentElement.clientHeight){
		document.getElementById('effortPos').style.top = document.documentElement.clientWidth/100*27+'px';
		document.getElementById('effort').style.fontSize = document.documentElement.clientWidth/100*2.5+'px';
	}
	else {
		document.getElementById('effortPos').style.top = document.documentElement.clientWidth/100*60+'px';
		document.getElementById('effort').style.fontSize = document.documentElement.clientWidth/100*3.5+'px';
	}
}

size();

window.addEventListener('resize', function() {
  size();
}, false);

var scr = window.matchMedia("all and (orientation:portrait)");

scr.addListener(function(){
	window.addEventListener('resize', function() {
	  size();
	}, false);
});


